// Calculates the minimum time required to solve 'c' problems with the given constraints.
// The strategy involves making clones (each clone can solve problems in parallel) and then solving the problems.
// Parameters:
//   a: Time taken to make one clone
//   b: Time taken to solve one problem
//   c: Total number of problems to solve
let calculateMinTime (a: int) (b: int) (c: int) =
    let mutable minTime = System.Int32.MaxValue
    // The maximum number of clones (n) we need to check is up to log2(c) + 2
    let maxN = if c > 0 then (Math.Log2(float c) |> int) + 2 else 0
    for n in 0..maxN do
        let k = 1 <<< n // Equivalent to 2^n
        let timeClone = n * a
        let problemsPerRanran = (c + k - 1) / k // Ceiling division: ceil(c / k)
        let timeProblems = problemsPerRanran * b
        let totalTime = timeClone + timeProblems
        if totalTime < minTime then
            minTime <- totalTime
    minTime

