open System

// find function with path compression
// Finds the root parent of element x in the parent array
let rec find (parent: int[]) (x: int) =
    if parent.[x] <> x then
        parent.[x] <- find parent parent.[x]
    parent.[x]

// smallestEquivalentString function
// Takes three strings: s1, s2 (defining equivalence relationships), and baseStr
// Returns the lexicographically smallest equivalent string for baseStr
let smallestEquivalentString (s1: string) (s2: string) (baseStr: string) =
    let parent = [|0..25|] // Initialize parent array for 26 letters (a-z)

    // Process each character pair in s1 and s2 to build equivalence classes
    for i in 0..s1.Length-1 do
        let a = int s1.[i] - int 'a'
        let b = int s2.[i] - int 'a'
        let rootA = find parent a
        let rootB = find parent b
        if rootA <> rootB then
            if rootA < rootB then
                parent.[rootB] <- rootA
            else
                parent.[rootA] <- rootB

    // Build the result string by finding the smallest equivalent for each character
    let result = 
        baseStr.ToCharArray()
        |> Array.map (fun c ->
            let idx = int c - int 'a'
            let root = find parent idx
            char (root + int 'a'))
        |> String

    result

open System

// computeProbability calculates the probability that exactly half of the selected members
// in a subset will be successful, given their individual probabilities.
// It uses dynamic programming to compute the probabilities efficiently.
let computeProbability (members: float list) (k: int) =
    let t = k / 2
    let dp = Array.create (k + 1) 0.0
    dp.[0] <- 1.0
    for p in members do
        for j in k .. -1 .. 1 do
            dp.[j] <- dp.[j] * (1.0 - p) + dp.[j - 1] * p
        dp.[0] <- dp.[0] * (1.0 - p)
    dp.[t]

// combinations generates all possible combinations of size k from the input list.
let rec combinations k lst =
    let rec comb acc k lst =
        match k, lst with
        | 0, _ -> [acc]
        | _, [] -> []
        | n, h::t -> (comb (h::acc) (n-1) t) @ (comb acc n t)
    comb [] k lst

// findBestProbability finds the maximum probability among all possible subsets of size k.
let findBestProbability (probabilities: float list) (k: int) =
    probabilities
    |> combinations k
    |> List.map (fun subset -> computeProbability subset k)
    |> List.max

