open System

// reverseString function returns the reverse of the string s. If the input string is a palindrome,
// the function returns "Palindrome". The function is case and space insensitive.
let reverseString (s: string) =
    // Remove spaces and convert to lowercase
    let cleanedS = s.Replace(" ", "").ToLower()

    // Check if cleaned string is a palindrome
    if cleanedS = new string(cleanedS.ToCharArray() |> Array.rev) then
        "Palindrome"
    else
    // Return the reverse of the original string if not a palindrome
        new string(s.ToCharArray() |> Array.rev)

