class LongestSubarrayFinder {
    /**
     * Finds the length of the longest subarray with sum less than or equal to S.
     * Uses a sliding window approach to efficiently compute the result.
     *
     * @param N Length of the sequence
     * @param S The target maximum sum
     * @param sequence List of non-negative integers
     * @return The length of the longest subarray meeting the condition
     */
    static int findLongestSubarrayWithSumLeqS(int N, int S, List<Integer> sequence) {
        int left = 0
        int currentSum = 0
        int maxLength = 0

        for (int right = 0; right < N; right++) {
            currentSum += sequence[right]

            // Shrink the window from the left if sum exceeds S
            while (currentSum > S && left <= right) {
                currentSum -= sequence[left]
                left++
            }

            // Update maxLength if current window is larger
            maxLength = Math.max(maxLength, right - left + 1)
        }

        return maxLength
    }
}

assertions:
void testFindLongestSubarrayWithSumLeqS() {
    // Test case 1: Normal case with a valid subarray
    assert LongestSubarrayFinder.findLongestSubarrayWithSumLeqS(5, 10, [4, 3, 1, 2, 5]) == 4

    // Test case 2: Case where all elements can be included
    assert LongestSubarrayFinder.findLongestSubarrayWithSumLeqS(5, 20, [1, 2, 3, 4, 5]) == 5

    // Test case 3: Case where no elements can be included
    assert LongestSubarrayFinder.findLongestSubarrayWithSumLeqS(5, 2, [5, 6, 7, 8, 9]) == 0

    // Test case 4: Empty sequence case
    assert LongestSubarrayFinder.findLongestSubarrayWithSumLeqS(0, 10, []) == 0

    // Test case 5: Case with multiple possible subarrays of same length
    assert LongestSubarrayFinder.findLongestSubarrayWithSumLeqS(6, 7, [2, 1, 3, 1, 1, 2]) == 4
}

