import java.text.DateFormat
import java.text.SimpleDateFormat
import java.util.Date

class TaskScheduler {
    // scheduleTasks sorts a list of tasks based on urgency (descending) and arrival time (ascending)
    // Each task is a map containing 'name', 'urgency', and 'arrivalTime' keys
    // Returns a sorted list of tasks
    // Throws IllegalArgumentException if any task is missing required fields or has incorrect timestamp format
    static List<Map> scheduleTasks(List<Map> tasks) {
        try {
            return tasks.sort { a, b ->
                // First compare by urgency (descending)
                def urgencyCompare = b.urgency <=> a.urgency
                if (urgencyCompare != 0) {
                    return urgencyCompare
                }

                // If urgencies are equal, compare by arrival time (ascending)
                def aTime = Date.parse('yyyy-MM-dd HH:mm:ss', a.arrivalTime).time
                def bTime = Date.parse('yyyy-MM-dd HH:mm:ss', b.arrivalTime).time
                return aTime <=> bTime
            }
        } catch (NullPointerException e) {
            throw new IllegalArgumentException("Missing required field: ${e.message}")
        } catch (IllegalArgumentException e) {
            throw new IllegalArgumentException("Incorrect timestamp format: ${e.message}")
        }
    }
}

assertions:
import groovy.transform.CompileStatic

@CompileStatic
void testTaskScheduler() {
    // Test case 1: Normal case with different urgencies and arrival times
    def testCase1 = [
        [name: 'Task1', urgency: 3, arrivalTime: '2023-01-01 10:00:00'],
        [name: 'Task2', urgency: 1, arrivalTime: '2023-01-01 09:00:00'],
        [name: 'Task3', urgency: 3, arrivalTime: '2023-01-01 08:00:00'],
        [name: 'Task4', urgency: 2, arrivalTime: '2023-01-01 07:00:00']
    ]
    def result1 = TaskScheduler.scheduleTasks(testCase1)
    assert result1*.name == ['Task3', 'Task1', 'Task4', 'Task2']

    // Test case 2: Tasks with same urgency but different arrival times
    def testCase2 = [
        [name: 'TaskA', urgency: 2, arrivalTime: '2023-01-02 12:00:00'],
        [name: 'TaskB', urgency: 2, arrivalTime: '2023-01-02 11:00:00'],
        [name: 'TaskC', urgency: 2, arrivalTime: '2023-01-02 10:00:00']
    ]
    def result2 = TaskScheduler.scheduleTasks(testCase2)
    assert result2*.name == ['TaskC', 'TaskB', 'TaskA']

    // Test case 3: Single task
    def testCase3 = [
        [name: 'SingleTask', urgency: 5, arrivalTime: '2023-01-03 00:00:00']
    ]
    def result3 = TaskScheduler.scheduleTasks(testCase3)
    assert result3*.name == ['SingleTask']

    // Test case 4: Error case - missing field
    def errorCase1 = [
        [name: 'BadTask', arrivalTime: '2023-01-01 10:00:00'] // missing urgency
    ]
    try {
        TaskScheduler.scheduleTasks(errorCase1)
        assert false: "Should have thrown IllegalArgumentException"
    } catch (IllegalArgumentException e) {
        assert e.message.contains("Missing required field")
    }

    // Test case 5: Error case - bad timestamp format
    def errorCase2 = [
        [name: 'BadTask', urgency: 1, arrivalTime: '2023-01-01'] // missing time portion
    ]
    try {
        TaskScheduler.scheduleTasks(errorCase2)
        assert false: "Should have thrown IllegalArgumentException"
    } catch (IllegalArgumentException e) {
        assert e.message.contains("Incorrect timestamp format")
    }
}

testTaskScheduler()

