class LongestSubstringWithKDeletions {
    /**
     * Calculates the length of the longest substring containing only one unique character
     * after making at most k deletions.
     * @param s The input string (must contain only 'a's and 'b's)
     * @param k The maximum number of allowed deletions
     * @return The length of the longest valid substring
     */
    static int longestSubstringWithKDeletions(String s, int k) {
        int n = s.length()
        if (n == 0) {
            return 0
        }

        /**
         * Helper function to calculate the longest substring where all characters
         * could be replaced with the specified character using at most k deletions
         * @param charToKeep The character we're trying to keep in the substring
         * @return The maximum length of such substring
         */
        def longestSubstringWithKReplacements = { charToKeep ->
            int maxLength = 0
            int left = 0
            int maxCount = 0
            Map<Character, Integer> counts = ['a': 0, 'b': 0]

            for (int right = 0; right < n; right++) {
                counts[s[right]] = counts.getOrDefault(s[right], 0) + 1
                maxCount = Math.max(maxCount, counts[charToKeep])
                while ((right - left + 1) - maxCount > k) {
                    counts[s[left]] = counts[s[left]] - 1
                    left++
                }
                maxLength = Math.max(maxLength, right - left + 1)
            }
            return maxLength
        }

        return Math.max(longestSubstringWithKReplacements('a'), 
                       longestSubstringWithKReplacements('b'))
    }
}

