class MainClass {
    /**
     * Calculates the sum of the digits of a number raised to the power of their respective positions.
     * For example, for the number 123, the sum is 1^1 + 2^2 + 3^3 = 1 + 4 + 27 = 32.
     *
     * @param n A positive integer
     * @return The sum of the digits raised to the power of their respective positions
     */
    static def sumOfDigitsPower(int n) {
        def nStr = n.toString()
        def totalSum = 0
        nStr.eachWithIndex { digit, idx ->
            totalSum += (digit.toInteger()) ** (idx + 1)
        }
        return totalSum
    }
}

