     *
     * @param n A positive integer
     * @return The sum of the digits raised to the power of their respective positions
     */
    static def sumOfDigitsPower(int n) {
        def nStr = n.toString()
        def totalSum = 0
        nStr.eachWithIndex { digit, idx ->
            totalSum += (digit.toInteger()) ** (idx + 1)
        }
        return totalSum
    }
}
