class MainClass {
    // isPerfectPowerOfBase checks if the given number is a perfect power of the specified base.
    // A number is a perfect power of a base if it can be expressed as base^power where power is an integer.
    // Returns true if the number is a perfect power of the base, false otherwise.
    // If the number is less than 1 or the base is less than 2, it returns false.
    static boolean isPerfectPowerOfBase(int number, int base) {
        if (number < 1 || base < 2) {
            return false
        }

        int power = 0
        while (Math.pow(base, power) < number) {
            power += 1
        }

        return Math.pow(base, power) == number
    }
}

