class ViewingPlatformAnalyzer {
    /**
     * Calculates the maximum size of any viewing platform that can be formed from a list of heights.
     * A viewing platform is defined as a sequence of positions where each subsequent position
     * has a height greater than or equal to the minimum height in the sequence so far.
     *
     * @param heights List of heights (as integers)
     * @return The size of the largest valid viewing platform found
     */
    static int findMaxViewingPlatform(List<Integer> heights) {
        int n = heights.size()
        if (n == 0) {
            return 0
        }

        int maxPlatformSize = 1

        for (int start = 0; start < n; start++) {
            int currentMin = heights[start]
            int currentPlatformSize = 1

            for (int end = start + 1; end < n; end++) {
                if (heights[end] >= currentMin) {
                    currentPlatformSize++
                    maxPlatformSize = Math.max(maxPlatformSize, currentPlatformSize)
                    currentMin = heights[end]
                } else {
                    break
                }
            }
        }

        return maxPlatformSize
    }
}

