    assert MainClass.isPalindrome(121) == true
    assert MainClass.isPalindrome(123) == false
    assert MainClass.isPalindrome(1) == true
    assert MainClass.isPalindrome(12321) == true
    assert MainClass.isPalindrome(12345) == false

    // Test generateRandomNumber
    def randomNum1 = MainClass.generateRandomNumber(1, 10)
    assert randomNum1 >= 1 && randomNum1 <= 10

    def randomNum2 = MainClass.generateRandomNumber(5, 5)
    assert randomNum2 == 5

    def randomNum3 = MainClass.generateRandomNumber(10, 20)
    assert randomNum3 >= 10 && randomNum3 <= 20

    try {
        MainClass.generateRandomNumber(10, 1)
        assert false : "Expected IllegalArgumentException"
    } catch (IllegalArgumentException e) {
        assert e.message == "rangeStart must be less than or equal to rangeEnd"
    }

    // Test checkCriteria
    def criteria = [
        { num -> num % 2 == 0 },  // Check if number is even
        { num -> num > 10 },      // Check if number is greater than 10
        { num -> MainClass.isPalindrome(num) }  // Check if number is palindrome
    ]

    assert MainClass.checkCriteria(12, criteria) == false
    assert MainClass.checkCriteria(11, criteria) == false
    assert MainClass.checkCriteria(22, criteria) == true
    assert MainClass.checkCriteria(121, criteria) == false
    assert MainClass.checkCriteria(8, criteria) == false
    assert MainClass.checkCriteria(131, criteria) == false
}

