class FlightScheduler {
    /**
     * Checks if it's possible to schedule all flights such that the gap between any
     * two consecutive flights is at least k minutes.
     *
     * @param n Number of flights
     * @param k Minimum required gap in minutes between consecutive flights
     * @param times List of flight start times in minutes from start of day
     * @return "Yes" if scheduling is possible, "No" otherwise
     */
    static String canScheduleFlights(int n, int k, List<Integer> times) {
        for (int i = 1; i < n; i++) {
            if (times[i] - times[i-1] < k) {
                return "No"
            }
        }
        return "Yes"
    }
}

