class ShortestPathInGrid {
    /**
     * Finds the minimum number of steps required to get from the top-left corner
     * to the bottom-right corner in a grid, or -1 if it's not possible.
     * @param grid A 2D list representing the grid where 0 is passable and 1 is blocked
     * @return The minimum number of steps or -1 if no path exists
     */
    static int shortestPath(List<List<Integer>> grid) {
        if (!grid || !grid[0] || grid[0][0] == 1 || grid[-1][-1] == 1) {
            return -1
        }

        int m = grid.size()
        int n = grid[0].size()
        def directions = [[0, 1], [1, 0], [0, -1], [-1, 0]]

        def queue = [] as ArrayDeque
        queue.add([0, 0, 1])  // [x, y, steps]
        def visited = new HashSet()
        visited.add([0, 0])

        while (!queue.isEmpty()) {
            def (x, y, steps) = queue.poll()

            if (x == m - 1 && y == n - 1) {
                return steps
            }

            directions.each { dx, dy ->
                int nx = x + dx
                int ny = y + dy
                if (nx >= 0 && nx < m && ny >= 0 && ny < n && grid[nx][ny] == 0 && !visited.contains([nx, ny])) {
                    queue.add([nx, ny, steps + 1])
                    visited.add([nx, ny])
                }
            }
        }

        return -1
    }
}

