class MainClass {
    /**
     * Calculates the maximum number of events one can attend from a given list of events.
     * Each event has a start day and an end day, and only one event can be attended per day.
     * The events are sorted by their end days to prioritize events that end earlier.
     *
     * @param events List of lists, where each sublist contains two integers [startDay, endDay]
     *               representing the start and end day of an event.
     * @return Maximum number of events that can be attended.
     */
    static int maxEvents(List<List<Integer>> events) {
        // Sort events by their end days
        events.sort { a, b -> a[1] <=> b[1] }
        Set<Integer> attendedDays = [] as Set
        int maxEvents = 0

        for (event in events) {
            int start = event[0]
            int end = event[1]
            for (day in start..end) {
                if (!attendedDays.contains(day)) {
                    attendedDays.add(day)
                    maxEvents++
                    break
                }
            }
        }

        return maxEvents
    }
}

