class MatchOverlapCounter {
    /**
     * Counts the number of matches that overlap with each query period.
     * A match is considered overlapping with a query if:
     * - The match starts before or at the query end time AND
     * - The match ends after or at the query start time
     *
     * @param matches List of match periods as [start, end] pairs
     * @param queries List of query periods as [start, end] pairs
     * @return List of counts where each element represents the number of overlapping matches for the corresponding query
     */
    static List<Integer> countOverlappingMatches(List<List<Integer>> matches, List<List<Integer>> queries) {
        List<Integer> overlapCounts = []
        queries.each { query ->
            def (qStart, qEnd) = query
            int count = 0
            matches.each { match ->
                def (start, end) = match
                if (qStart <= end && qEnd >= start) {
                    count++
                }
            }
            overlapCounts.add(count)
        }
        return overlapCounts
    }
}

