class MainClass {
    /**
     * Processes a list of operations and returns the results of query operations.
     * The operations can be either 'add x' to add a number to a sorted list,
     * or 'query k' to get the k-th smallest element (1-based index).
     *
     * @param operations List of operations in the format ['add x', 'query k']
     * @return List of results for query operations
     */
    static List<Integer> processOperations(List<String> operations) {
        def lst = [] as List<Integer>
        def results = [] as List<Integer>

        operations.each { op ->
            if (op.startsWith("add")) {
                def x = op.split()[1] as Integer
                // Find insertion point to maintain sorted order
                def index = Collections.binarySearch(lst, x)
                if (index < 0) {
                    index = -index - 1
                }
                lst.add(index, x)
            } else if (op.startsWith("query")) {
                def k = op.split()[1] as Integer
                results.add(lst[k - 1])
            }
        }

        return results
    }
}

