class SmallestDivisorFinder {
    // Finds the smallest divisor such that the sum of divisions is less than or equal to threshold
    // Uses binary search to efficiently find the smallest divisor
    static int findSmallestDivisor(List<Integer> nums, int threshold) {
        // Helper function to compute the sum of ceiling divisions
        def computeSum = { int value ->
            nums.sum { num -> Math.ceil(num / value) as int }
        }

        // Binary search initialization
        int left = 1
        int right = nums.max()

        // Binary search loop
        while (left < right) {
            int mid = (left + right) / 2
            if (computeSum(mid) > threshold) {
                left = mid + 1
            } else {
                right = mid
            }
        }
        return left
    }
}

