class PathNameProcessor {
    /**
     * Extracts the base filename without extension from a given path.
     * This mimics Python's os.path.basename and splitext functionality.
     * For example, given "/path/to/file.txt" it returns "file".
     *
     * @param pathname The full path including filename
     * @return The filename without extension
     */
    static String basenameWithoutExtension(String pathname) {
        // Handle null or empty input
        if (pathname == null || pathname.isEmpty()) {
            return ""
        }

        // Extract the filename with extension
        def base = new File(pathname).getName()

        // Split name and extension
        def lastDotIndex = base.lastIndexOf('.')
        if (lastDotIndex > 0) {
            return base.substring(0, lastDotIndex)
        }
        return base
    }
}

