    assert PathNameProcessor.basenameWithoutExtension(null) == ""
    assert PathNameProcessor.basenameWithoutExtension("/just/path/") == "path"
    assert PathNameProcessor.basenameWithoutExtension("no_extension") == "no_extension"
}

