    // evenDigitSum calculates the sum of all even digits in a given integer.
    // If there are no even digits, it returns 0.
    static def evenDigitSum(int n) {
        def sumEvenDigits = 0
        // Convert the integer to a string to iterate over each digit
        n.toString().each { digitChar ->
            def digit = digitChar.toInteger()
            // Check if the digit is even
            if (digit % 2 == 0) {
                sumEvenDigits += digit
            }
        }
        return sumEvenDigits
    }
}

