class MainClass {
    // Aggregate class wraps a list of data and provides an iterator for it
    static class Aggregate {
        private List data

        Aggregate(List data) {
            this.data = data
        }

        Iterator iterator() {
            return new AggregateIterator(data)
        }
    }

    // AggregateIterator implements the Iterator interface to traverse the Aggregate data
    static class AggregateIterator implements Iterator {
        private List data
        private int index = 0

        AggregateIterator(List data) {
            this.data = data
        }

        boolean hasNext() {
            return index < data.size()
        }

        Object next() {
            if (!hasNext()) {
                throw new NoSuchElementException()
            }
            return data[index++]
        }
    }
}

assertions:
void testAggregateIterator() {
    // Test normal case with integers
    def intAggregate = new MainClass.Aggregate([1, 2, 3, 4, 5])
    def intIterator = intAggregate.iterator()
    def intResult = []
    while (intIterator.hasNext()) {
        intResult << intIterator.next()
    }
    assert intResult == [1, 2, 3, 4, 5]
    try {
        intIterator.next()
        assert false : "Should have thrown NoSuchElementException"
    } catch (NoSuchElementException e) {
        assert true
    }

    // Test case with strings
