class MainClass {
    /**
     * Returns the number of unique connected components of '1's in the grid.
     * Uses Depth-First Search (DFS) to explore connected '1's.
     *
     * @param n The number of rows in the grid.
     * @param m The number of columns in the grid.
     * @param grid The 2D grid of characters ('0's and '1's).
     * @return The number of connected components of '1's.
     */
    static int numConnectedComponents(int n, int m, List<List<String>> grid) {
        // Initialize a visited matrix to keep track of visited cells
        boolean[][] visited = new boolean[n][m]
        for (int i = 0; i < n; i++) {
            visited[i] = new boolean[m]
        }

        int components = 0

        // Directions for moving in 4 possible directions (up, down, left, right)
        def directions = [[0, 1], [1, 0], [0, -1], [-1, 0]]

        // Iterate through each cell in the grid
        for (int i = 0; i < n; i++) {
            for (int j = 0; j < m; j++) {
                if (grid[i][j] == '1' && !visited[i][j]) {
                    components++
                    visited[i][j] = true
                    // Use a stack for DFS
                    def stack = []
                    stack.push([i, j])

                    while (!stack.isEmpty()) {
                        def (cx, cy) = stack.pop()
                        // Explore all 4 directions
                        directions.each { dx, dy ->
                            int nx = cx + dx
                            int ny = cy + dy
                            if (nx >= 0 && nx < n && ny >= 0 && ny < m && grid[nx][ny] == '1' && !visited[nx][ny]) {
                                visited[nx][ny] = true
                                stack.push([nx, ny])
                            }
                        }
                    }
                }
            }
        }

        return components
    }
}

