class MainClass {
    // maxSubarraySumWithPositive calculates the maximum subarray sum for each query using Kadane's algorithm.
    // Each query consists of a list of numbers, and the function returns the maximum sum of any contiguous subarray.
    // If all numbers are negative, the maximum sum will be the least negative number (or -infinity if empty).
    static List<Integer> maxSubarraySumWithPositive(List<List<Object>> queries) {
        List<Integer> results = []

        for (query in queries) {
            int n = query[0] as int
            List<Integer> arr = query[1] as List<Integer>
            int maxSum = Integer.MIN_VALUE
            int currentSum = 0

            for (number in arr) {
                currentSum += number
                if (currentSum > maxSum) {
                    maxSum = currentSum
                }
                if (currentSum < 0) {
                    currentSum = 0
                }
            }

            results.add(maxSum)
        }

        return results
    }
}

