class EventScheduler {
    /**
     * Removes conflicting events from a list of events to create a non-conflicting schedule.
     * Events are sorted by end time, and then selected if they don't conflict with the last selected event.
     *
     * @param events List of event tuples where each tuple contains start and end times as integers
     * @return List of non-conflicting event tuples
     */
    static List<List<Integer>> removeConflicts(List<List<Integer>> events) {
        // Sort events by their end times (second element in each sub-list)
        events.sort { a, b -> a[1] <=> b[1] }

        // Initialize the list of non-conflicting events
        List<List<Integer>> nonConflicting = []

        // Initialize the end time of the last added event to -1
        int lastEndTime = -1

        for (event in events) {
            int start = event[0]
            int end = event[1]

            // If the start time is after the end time of the last added event
            if (start >= lastEndTime) {
                // Add the current event to the non-conflicting schedule
                nonConflicting.add(event)
                // Update the end time of the last added event
                lastEndTime = end
            }
        }

        return nonConflicting
    }
}

