class MainClass {
    /**
     * Merges two strings in a zigzag pattern, alternating characters from each string.
     * If one string is longer, appends the remaining characters after zigzag merge.
     *
     * @param word1 The first string.
     * @param word2 The second string.
     * @return A new string formed by merging word1 and word2 in zigzag pattern.
     */
    static String mergeStringsZigzag(String word1, String word2) {
        StringBuilder mergedString = new StringBuilder()
        int len1 = word1.length()
        int len2 = word2.length()
        int i = 0

        while (i < len1 && i < len2) {
            mergedString.append(word1.charAt(i))
            mergedString.append(word2.charAt(i))
            i++
        }

        // Append the remaining part of the longer string
        if (i < len1) {
            mergedString.append(word1.substring(i))
        } else if (i < len2) {
            mergedString.append(word2.substring(i))
        }

        return mergedString.toString()
    }
}

