class MainClass {
    // maxIncreasingSubsequenceLength calculates the length of the longest strictly increasing subsequence
    // from a given list of numbers. It uses a dynamic programming approach with binary search
    // to efficiently determine the longest increasing subsequence.
    static int maxIncreasingSubsequenceLength(List<Integer> columns) {
        // This will hold the end of the longest increasing subsequence at each length
        List<Integer> dp = []

        for (int height in columns) {
            // Find the position where the height can be inserted to maintain the increasing order
            int pos = Collections.binarySearch(dp, height)
            if (pos < 0) {
                pos = -(pos + 1)
            }

            if (pos == dp.size()) {
                dp.add(height)
            } else {
                dp[pos] = height
            }
        }

        return dp.size()
    }
}

