class MainClass {
    // longestDecreasingSubsequence calculates the length of the longest decreasing subsequence in a list of integers.
    // It uses dynamic programming to solve the problem.
    // If the input list is empty, it returns 0.
    static int longestDecreasingSubsequence(List<Integer> arr) {
        if (arr.isEmpty()) {
            return 0
        }

        int n = arr.size()
        List<Integer> dp = new ArrayList<>(Collections.nCopies(n, 1)) // Each element is a subsequence of length 1 by itself

        for (int i = 1; i < n; i++) {
            for (int j = 0; j < i; j++) {
                if (arr[j] > arr[i]) {
                    dp[i] = Math.max(dp[i], dp[j] + 1)
                }
            }
        }

        return Collections.max(dp)
    }
}

