class MaxCliqueInfluence {
    /**
     * Calculates the maximum influence of a clique of size k in a graph.
     * A clique is a subset of nodes where every two distinct nodes are connected.
     * The influence is the sum of influence values of all nodes in the clique.
     *
     * @param m Number of nodes in the graph
     * @param influences List of influence values for each node (1-based index)
     * @param connections List of edges connecting the nodes
     * @param k Size of the clique to look for
     * @return Maximum influence found, or -1 if no clique of size k exists
     */
    static int maxInfluenceOfClique(int m, List<Integer> influences, List<List<Integer>> connections, int k) {
        // Build the graph as an adjacency list
        Map<Integer, Set<Integer>> graph = [:]
        (1..m).each { graph[it] = new HashSet<Integer>() }

        connections.each { edge ->
            int u = edge[0], v = edge[1]
            graph[u].add(v)
            graph[v].add(u)
        }

        int maxInfluence = -1
        List<Integer> nodes = (1..m).toList()

        // Generate all possible combinations of k nodes
        combinations(nodes, k).each { clique ->
            // Check if the combination forms a clique (all nodes connected to each other)
            boolean isClique = true
            for (int i = 0; i < clique.size() && isClique; i++) {
                for (int j = i + 1; j < clique.size() && isClique; j++) {
                    if (!graph[clique[i]].contains(clique[j])) {
                        isClique = false
                    }
                }
            }

            if (isClique) {
                // Calculate the influence sum for this clique
                int currentInfluence = clique.sum { influences[it - 1] }
                maxInfluence = Math.max(maxInfluence, currentInfluence)
            }
        }

        return maxInfluence
    }

    /**
     * Generates all combinations of size k from the input list.
     * This replaces Python's itertools.combinations functionality.
     *
     * @param items List of items to combine
     * @param k Size of each combination
     * @return List of all combinations (each combination is a list)
     */
    private static List<List<Integer>> combinations(List<Integer> items, int k) {
        if (k == 0) return [[]]
        if (items.isEmpty()) return []

        def head = items[0]
        def tail = items.subList(1, items.size())

        def result = []
        combinations(tail, k - 1).each { combo ->
            result << ([head] + combo)
        }
        result.addAll(combinations(tail, k))

        return result
    }
}

