class FertileRegionsCounter {
    // Performs a depth-first search to mark all connected fertile cells (1's) as visited (0's)
    // x and y are the current coordinates in the field
    static void dfs(List<List<Integer>> field, int x, int y) {
        // Check if current coordinates are out of bounds or cell is not fertile
        if (x < 0 || y < 0 || x >= field.size() || y >= field[0].size() || field[x][y] == 0) {
            return
        }
        // Mark current cell as visited
        field[x][y] = 0
        // Explore all four adjacent cells (up, down, left, right)
        dfs(field, x + 1, y)
        dfs(field, x - 1, y)
        dfs(field, x, y + 1)
        dfs(field, x, y - 1)
    }

    // Counts the number of contiguous fertile regions (groups of 1's) in a 2D field
    static int countFertileRegions(List<List<Integer>> field) {
        int count = 0
        // Iterate through each cell in the field
        for (int i = 0; i < field.size(); i++) {
            for (int j = 0; j < field[i].size(); j++) {
                if (field[i][j] == 1) {
                    // Found a new fertile region, perform DFS to mark all connected cells
                    dfs(field, i, j)
                    count++
                }
            }
        }
        return count
    }
}

