class BinaryStringConverter {
    /**
     * Calculates the minimum length of a substring that needs to be replaced
     * to convert the entire string into a binary string with no consecutive '1's.
     * It counts the maximum number of consecutive '1's in the string.
     * 
     * @param s The input binary string to analyze
     * @return The length of the longest sequence of consecutive '1's
     */
    static int minLengthNoConsecutiveOnes(String s) {
        int maxConsecutiveOnes = 0
        int currentOnes = 0

        for (char c in s) {
            if (c == '1') {
                currentOnes++
                if (currentOnes > maxConsecutiveOnes) {
                    maxConsecutiveOnes = currentOnes
                }
            } else {
                currentOnes = 0
            }
        }

        return maxConsecutiveOnes
    }
}

