class BeautifulStringCost {
    /**
     * Calculates the minimum cost to make a string beautiful by ensuring no two adjacent characters are the same.
     * A beautiful string has no consecutive identical characters. The cost is the number of replacements needed.
     * @param n The length of the string
     * @param s The input string to be processed
     * @return The minimum cost to make the string beautiful
     */
    static int minCostToBeautifulString(int n, String s) {
        int minCost = 0
        int i = 0
        StringBuilder sb = new StringBuilder(s)

        while (i < n - 1) {
            if (sb.charAt(i) == sb.charAt(i + 1)) {
                minCost++
                if (i + 2 < n) {
                    if (sb.charAt(i) != 'a' && sb.charAt(i + 2) != 'a') {
                        sb.setCharAt(i + 1, 'a' as char)
                    } else if (sb.charAt(i) != 'b' && sb.charAt(i + 2) != 'b') {
                        sb.setCharAt(i + 1, 'b' as char)
                    } else {
                        sb.setCharAt(i + 1, 'c' as char)
                    }
                } else {
                    if (sb.charAt(i) != 'a') {
                        sb.setCharAt(i + 1, 'a' as char)
                    } else if (sb.charAt(i) != 'b') {
                        sb.setCharAt(i + 1, 'b' as char)
                    } else {
                        sb.setCharAt(i + 1, 'c' as char)
                    }
                }
            }
            i++
        }

        return minCost
    }
}

