        while (i < n - 1) {
            if (sb.charAt(i) == sb.charAt(i + 1)) {
                minCost++
                if (i + 2 < n) {
                    if (sb.charAt(i) != 'a' && sb.charAt(i + 2) != 'a') {
                        sb.setCharAt(i + 1, 'a' as char)
                    } else if (sb.charAt(i) != 'b' && sb.charAt(i + 2) != 'b') {
                        sb.setCharAt(i + 1, 'b' as char)
                    } else {
                        sb.setCharAt(i + 1, 'c' as char)
                    }
                } else {
                    if (sb.charAt(i) != 'a') {
                        sb.setCharAt(i + 1, 'a' as char)
                    } else if (sb.charAt(i) != 'b') {
                        sb.setCharAt(i + 1, 'b' as char)
                    } else {
                        sb.setCharAt(i + 1, 'c' as char)
                    }
                }
            }
            i++
        }

        return minCost
    }
}
