class VisitorCounter {
    /**
     * Calculates the number of visitors within each query time range.
     * Uses a prefix sum approach for efficient range queries.
     * 
     * @param entryTimes List of entry times for visitors
     * @param queries List of query ranges as [start, end] pairs
     * @return List containing visitor counts for each query range
     */
    static List<Integer> countVisitors(List<Integer> entryTimes, List<List<Integer>> queries) {
        // Create a frequency map of entry times
        def timeCounter = entryTimes.countBy { it } as Map<Integer, Integer>

        // Create prefix sum array (assuming max time is 105 as per original code)
        int maxTime = 105
        def prefixSum = new int[maxTime + 1]

        // Fill the prefix sum array
        for (time in 0..maxTime) {
            prefixSum[time] = (time == 0 ? 0 : prefixSum[time - 1]) + (timeCounter.get(time, 0))
        }

        // Process each query
        def results = []
        queries.each { query ->
            def start = query[0]
            def end = query[1]
            if (start == 0) {
                results.add(prefixSum[end])
            } else {
                results.add(prefixSum[end] - prefixSum[start - 1])
            }
        }

        return results
    }
}

