class ParenthesesDepth {
    /**
     * Calculates the maximum depth of balanced parentheses in a string after inserting
     * exactly one pair of parentheses. The insertion is assumed to increase the maximum
     * depth by 1, as the original string is balanced.
     *
     * @param s A balanced parentheses string
     * @return The maximum depth after inserting one pair of parentheses
     */
    static int maxDepthAfterInsertion(String s) {
        int currentDepth = 0
        int maxDepth = 0

        for (char c in s) {
            if (c == '(') {
                currentDepth++
                maxDepth = Math.max(maxDepth, currentDepth)
            } else if (c == ')') {
                currentDepth--
            }
        }

        return maxDepth + 1
    }
}

