class MainClass {
    /**
     * Returns the length of the longest balanced subarray.
     * A balanced subarray is defined as an array whose sum is even.
     *
     * @param arr The input list of integers.
     * @return The length of the longest balanced subarray.
     */
    static int maxBalancedSubarrayLength(List<Integer> arr) {
        int n = arr.size()
        if (n == 0) {
            return 0
        }

        int totalSum = arr.sum()

        if (totalSum % 2 == 0) {
            return n
        }

        for (int i = 0; i < n; i++) {
            if (arr[i] % 2 != 0) {
                // Removing this odd number would balance the array
                return n - 1
            }
        }
        return 0
    }
}

