class IslandCounter {
    // Counts the number of distinct islands in a 2D grid.
    // An island is a group of adjacent 1s (connected horizontally or vertically).
    // Islands are considered distinct if their shapes cannot be translated to match each other.
    static int numDistinctIslands(int m, int n, List<List<Integer>> grid) {
        // Uses BFS to explore each island and records relative positions of land cells
        // to determine unique shapes while ignoring positional differences

        Set<List<Integer>> seen = [] as Set
        Set<List<List<Integer>>> distinctIslands = [] as Set

        for (int i = 0; i < m; i++) {
            for (int j = 0; j < n; j++) {
                if (grid[i][j] == 1 && !seen.contains([i, j])) {
                    List<List<Integer>> islandShape = bfs(i, j, m, n, grid, seen)
                    distinctIslands.add(islandShape)
                }
            }
        }

        return distinctIslands.size()
    }

    // Performs BFS to explore an island starting from given coordinates
    // Returns the island shape as relative positions from the starting point
    private static List<List<Integer>> bfs(int r, int c, int m, int n, 
                                         List<List<Integer>> grid, Set<List<Integer>> seen) {
        List<List<Integer>> relativePositions = []
        List<List<Integer>> queue = [[r, c]]
        seen.add([r, c])

        while (!queue.isEmpty()) {
            def (x, y) = queue.remove(0)
            relativePositions.add([x - r, y - c])

            // Check all four directions
            [[-1, 0], [1, 0], [0, -1], [0, 1]].each { dx, dy ->
                int nx = x + dx
                int ny = y + dy
                if (nx >= 0 && nx < m && ny >= 0 && ny < n && 
                    grid[nx][ny] == 1 && !seen.contains([nx, ny])) {
                    seen.add([nx, ny])
                    queue.add([nx, ny])
                }
            }
        }

        // Sort the positions to make the shape representation consistent
        return relativePositions.sort()
    }
}

