class SubArrayChecker {
    /**
     * Checks if there exists a subarray with sum equal to zero.
     * It uses a set to keep track of prefix sums encountered so far.
     * If a prefix sum repeats, it means there is a subarray with sum zero.
     *
     * @param arr List of integers to check for subarray with zero sum.
     * @return true if a subarray with zero sum exists, false otherwise.
     */
    static boolean subArrayExists(List<Integer> arr) {
        Set<Integer> presumSet = new HashSet<>()
        presumSet.add(0)
        int presum = 0

        for (int num in arr) {
            presum += num
            if (presumSet.contains(presum)) {
                return true
            }
            presumSet.add(presum)
        }

        return false
    }
}

