class UniquePathsCalculator {
    // MOD is a constant used for modulo operation to prevent integer overflow
    static final int MOD = 1_000_000_007

    // Calculates the number of unique paths from top-left to bottom-right corner of a grid
    // while avoiding obstacles ('X') and moving only right or down.
    // The result is returned modulo 10^9 + 7.
    static int countUniquePaths(List<String> grid) {
        if (grid.isEmpty()) return 0

        int n = grid.size()
        int m = grid[0].length()

        // Initialize DP table with zeros
        int[][] dp = new int[n][m]

        // Starting position (top-left corner)
        dp[0][0] = grid[0][0] == 'X' ? 0 : 1

        // Fill the DP table
        for (int i = 0; i < n; i++) {
            for (int j = 0; j < m; j++) {
                // Skip if it's the starting position (already initialized)
                if (i == 0 && j == 0) continue

                if (grid[i][j] == 'X') {
                    dp[i][j] = 0
                } else {
                    int fromTop = (i > 0) ? dp[i-1][j] : 0
                    int fromLeft = (j > 0) ? dp[i][j-1] : 0
                    dp[i][j] = (fromTop + fromLeft) % MOD
                }
            }
        }

        return dp[n-1][m-1]
    }
}

