                if (grid[i][j] == 'X') {
                    dp[i][j] = 0
                } else {
                    int fromTop = (i > 0) ? dp[i-1][j] : 0
                    int fromLeft = (j > 0) ? dp[i][j-1] : 0
                    dp[i][j] = (fromTop + fromLeft) % MOD
                }
            }
        }

        return dp[n-1][m-1]
    }
}
void testUniquePathsCalculator() {
    // Test case 1: 3x3 grid with one obstacle in center
    assert UniquePathsCalculator.countUniquePaths(["...", ".X.", "..."]) == 2

    // Test case 2: 3x3 grid with no obstacles
    assert UniquePathsCalculator.countUniquePaths(["...", "...", "..."]) == 6

    // Test case 3: 3x4 grid with two obstacles
    assert UniquePathsCalculator.countUniquePaths(["...X", "....", "X..."]) == 8

    // Test case 4: 1x1 grid with obstacle
    assert UniquePathsCalculator.countUniquePaths(["X"]) == 0

    // Test case 5: 1x1 grid without obstacle
    assert UniquePathsCalculator.countUniquePaths(["."]) == 1
}

