class StairClimbingCost {
    /**
     * Calculates the minimum cost to climb stairs where each step has an associated cost.
     * The cost to reach step i is the cost at step i plus the minimum cost to reach step i-1 or i-2.
     * The final step can be reached from either the last step or the second-to-last step.
     *
     * @param cost List of costs for each step
     * @return Minimum cost to reach the top of the stairs
     */
    static int minCostClimbingStairs(List<Integer> cost) {
        int n = cost.size()
        if (n == 0) {
            return 0
        } else if (n == 1) {
            return cost[0]
        }

        int[] dp = new int[n]
        dp[0] = cost[0]
        dp[1] = cost[1]

        for (int i = 2; i < n; i++) {
            dp[i] = cost[i] + Math.min(dp[i - 1], dp[i - 2])
        }

        return Math.min(dp[n - 1], dp[n - 2])
    }

    /**
     * Processes multiple test cases of stair climbing cost problems.
     * Each test case consists of a number of steps and their associated costs.
     *
     * @param testCases List of test cases where each test case is a list of costs
     * @return List of minimum costs for each test case
     */
    static List<Integer> processTestCases(List<List<Integer>> testCases) {
        List<Integer> results = []
        testCases.each { testCase ->
            results.add(minCostClimbingStairs(testCase))
        }
        return results
    }
}

