    assert StairClimbingCost.minCostClimbingStairs([1, 100, 1, 1, 1, 100, 1, 1, 100, 1]) == 6
    assert StairClimbingCost.minCostClimbingStairs([0, 0, 0, 0]) == 0
    assert StairClimbingCost.minCostClimbingStairs([10]) == 10
    assert StairClimbingCost.minCostClimbingStairs([]) == 0

    // Test batch processing
    def batchResults = StairClimbingCost.processTestCases([
        [10, 15, 20],
        [1, 100, 1, 1, 1, 100, 1, 1, 100, 1],
        [0, 2, 2, 1]
    ])
    assert batchResults == [15, 6, 2]
}

