class MainClass {
    /**
     * Calculates the minimum sum of differences by pairing adjacent elements in a sorted list.
     * The list is sorted first, and then the differences between adjacent elements (paired in twos) are summed.
     *
     * @param n The number of elements in the skills list.
     * @param skills The list of skill values.
     * @return The minimum sum of differences.
     */
    static def minDiffSum(int n, List<Integer> skills) {
        skills.sort()
        def totalDiff = 0
        for (int i = 0; i < n; i += 2) {
            if (i + 1 < n) {
                totalDiff += skills[i + 1] - skills[i]
            }
        }
        return totalDiff
    }

    /**
     * Computes the minimum sum of differences for multiple test cases.
     *
     * @param t The number of test cases.
     * @param testCases A list of test cases, where each test case is a tuple of (n, skills).
     * @return A list of results for each test case.
     */
    static def minSumOfDifferences(int t, List<List> testCases) {
        def results = []
        for (int i = 0; i < t; i++) {
            def n = testCases[i][0]
            def skills = testCases[i][1]
            results.add(minDiffSum(n, skills))
        }
        return results
    }
}

