class MagicSquareChecker {
    // Checks if a 3x3 submatrix is a magic square
    // A magic square has all rows, columns, and diagonals summing to 15,
    // and contains all numbers from 1 to 9 exactly once
    static boolean isMagicSquare(List<List<Integer>> submatrix) {
        def s = 15  // Magic constant for 3x3 magic square

        // Check all rows sum to 15
        if (submatrix[0].sum() != s) return false
        if (submatrix[1].sum() != s) return false
        if (submatrix[2].sum() != s) return false

        // Check all columns sum to 15
        if (submatrix.collect { it[0] }.sum() != s) return false
        if (submatrix.collect { it[1] }.sum() != s) return false
        if (submatrix.collect { it[2] }.sum() != s) return false

        // Check both diagonals sum to 15
        if (submatrix[0][0] + submatrix[1][1] + submatrix[2][2] != s) return false
        if (submatrix[0][2] + submatrix[1][1] + submatrix[2][0] != s) return false

        // Check contains all numbers 1-9 exactly once
        def allNumbers = submatrix.flatten().sort()
        if (allNumbers != (1..9).toList()) return false

        return true
    }

    // Checks if any 3x3 submatrix in the given matrix is a magic square
    static boolean containsMagicSquare(List<List<Integer>> matrix) {
        def rows = matrix.size()
        if (rows < 3) return false
        def cols = matrix[0].size()
        if (cols < 3) return false

        for (int i = 0; i < rows - 2; i++) {
            for (int j = 0; j < cols - 2; j++) {
                // Extract 3x3 submatrix
                def submatrix = [
                    matrix[i].subList(j, j + 3),
                    matrix[i + 1].subList(j, j + 3),
                    matrix[i + 2].subList(j, j + 3)
                ]

                if (isMagicSquare(submatrix)) {
                    return true
                }
            }
        }
        return false
    }
}

