        return Math.sqrt(xDiff * xDiff + yDiff * yDiff)
    }

    // Finds the closest pair of points from a given list of points
    // Uses brute-force approach by checking all possible pairs
    // Returns a list containing the two closest points
    static List<List<Double>> findClosestPair(List<List<Double>> points) {
        if (points.size() < 2) {
            return null  // Not enough points to form a pair
        }

        double minDistance = Double.MAX_VALUE
        List<List<Double>> closestPair = null

        // Generate all possible pairs of points
        for (int i = 0; i < points.size() - 1; i++) {
            for (int j = i + 1; j < points.size(); j++) {
                def currentDistance = distance(points[i], points[j])
                if (currentDistance < minDistance) {
                    minDistance = currentDistance
                    closestPair = [points[i], points[j]]
                }
            }
        }

        return closestPair
    }
}
void testClosestPairFinder() {
    // Test case 1: Simple line
    def testCase1 = [[0.0, 0.0], [1.0, 1.0], [2.0, 2.0], [3.0, 3.0]]
    def result1 = ClosestPairFinder.findClosestPair(testCase1)
    assert result1 == [[0.0, 0.0], [1.0, 1.0]]
    assert ClosestPairFinder.distance(result1[0], result1[1]) == 1.4142135623730951

    // Test case 2: Unsorted points
    def testCase2 = [[0.0, 0.0], [5.0, 5.0], [1.0, 1.0], [2.0, 2.0]]
    def result2 = ClosestPairFinder.findClosestPair(testCase2)
    assert result2 == [[0.0, 0.0], [1.0, 1.0]]
    assert ClosestPairFinder.distance(result2[0], result2[1]) == 1.4142135623730951

    // Test case 3: 2D plane points
    def testCase3 = [[1.0, 1.0], [4.0, 5.0], [3.0, 4.0], [2.0, 3.0]]
    def result3 = ClosestPairFinder.findClosestPair(testCase3)
    assert result3 == [[4.0, 5.0], [3.0, 4.0]]
    assert ClosestPairFinder.distance(result3[0], result3[1]) == 1.4142135623730951

    // Test case 4: Negative coordinates
    def testCase4 = [[-1.0, -1.0], [2.0, 2.0], [0.0, 0.0], [1.0, 1.0]]
