class UniquePathsCalculator {
    /**
     * Calculates the number of unique paths from top-left to bottom-right in a grid with obstacles.
     * Obstacles are marked with '#', and empty cells are assumed to be marked with any other character.
     * Uses dynamic programming to count paths while avoiding obstacles.
     *
     * @param m Number of rows in the grid
     * @param n Number of columns in the grid
     * @param grid 2D list representing the grid with obstacles
     * @return Number of unique paths from (0,0) to (m-1,n-1)
     */
    static int countUniquePathsWithObstacles(int m, int n, List<List<String>> grid) {
        // Check if start or end position is blocked
        if (grid[0][0] == '#' || grid[m-1][n-1] == '#') {
            return 0
        }

        // Initialize DP table with zeros
        def dp = (0..<m).collect { (0..<n).collect { 0 } }
        dp[0][0] = 1  // Base case: one way to be at the start

        // Fill the DP table
        (0..<m).each { i ->
            (0..<n).each { j ->
                if (grid[i][j] == '#') {
                    dp[i][j] = 0  // No paths through obstacles
                } else {
                    if (i > 0) {
                        dp[i][j] += dp[i-1][j]  // Add paths from above
                    }
                    if (j > 0) {
                        dp[i][j] += dp[i][j-1]  // Add paths from left
                    }
                }
            }
        }

        return dp[m-1][n-1]  // Result is in the bottom-right cell
    }
}

assertions:
