    // Test case 2: 3x3 grid with one obstacle
    def grid2 = [['.', '.', '.'],
                ['.', '#', '.'],
                ['.', '.', '.']]
    assert UniquePathsCalculator.countUniquePathsWithObstacles(3, 3, grid2) == 2

    // Test case 3: 2x3 grid with blocked start
    def grid3 = [['#', '.', '.'],
                ['.', '.', '.']]
    assert UniquePathsCalculator.countUniquePathsWithObstacles(2, 3, grid3) == 0

    // Test case 4: 3x2 grid with blocked end
    def grid4 = [['.', '.'],
                ['.', '.'],
                ['.', '#']]
    assert UniquePathsCalculator.countUniquePathsWithObstacles(3, 2, grid4) == 0

    // Test case 5: 1x1 grid with no obstacle
    def grid5 = [['.']]
    assert UniquePathsCalculator.countUniquePathsWithObstacles(1, 1, grid5) == 1
}

