class MainClass {
    /**
     * Calculates the mean, variance, and standard deviation of a list of numbers.
     * 
     * @param inputList A list of numbers.
     * @return A list containing mean, variance, and standard deviation in that order.
     */
    static def calculateStats(List<Double> inputList) {
        if (inputList.size() == 0) {
            return [0.0, 0.0, 0.0]
        }
        def sum = inputList.sum()
        def mean = sum / inputList.size()
        def variance = inputList.collect { (it - mean) ** 2 }.sum() / inputList.size()
        def stdDev = Math.sqrt(variance)
        return [mean, variance, stdDev]
    }
}

