class MainClass {
    /**
     * Finds the length of the shortest substring that contains at least k occurrences of each character in the input list.
     * If no such substring exists, returns -1.
     * @param arr The input list of characters.
     * @param k The minimum required occurrences of each character in the substring.
     * @return The length of the shortest substring meeting the criteria, or -1 if no such substring exists.
     */
    static int shortestSubstringWithKOccurrences(List<Character> arr, int k) {
        int n = arr.size()
        if (k == 0) {
            return 0
        }

        // Count the frequency of each character in the input list
        Map<Character, Integer> requiredChars = arr.countBy { it }
        int uniqueChars = requiredChars.size()

        int left = 0
        int minLength = Integer.MAX_VALUE
        Map<Character, Integer> charCount = [:]
        int currentUniqueChars = 0

        for (int right = 0; right < n; right++) {
            char currentChar = arr[right]
            charCount[currentChar] = (charCount[currentChar] ?: 0) + 1

            if (charCount[currentChar] == k) {
                currentUniqueChars++
            }

            while (currentUniqueChars == uniqueChars) {
                minLength = Math.min(minLength, right - left + 1)
                char leftChar = arr[left]
                charCount[leftChar] = charCount[leftChar] - 1

                if (charCount[leftChar] == k - 1) {
                    currentUniqueChars--
                }
                left++
            }
        }

        return minLength != Integer.MAX_VALUE ? minLength : -1
    }
}

