class MainClass {
    /**
     * Filters a list of maps based on a threshold value of a specific key.
     * 
     * @param data List of maps to filter.
     * @param key The key to filter by.
     * @param threshold The threshold value.
     * @return A new list of maps with entries where the specified key's value is greater than the threshold.
     *         If the key is not present in a map, that entry is excluded.
     */
    static List<Map> filterData(List<Map> data, String key, Number threshold) {
        return data.findAll { entry -> 
            entry.containsKey(key) && entry.get(key) > threshold 
        }
    }
}

