    // findPeaks identifies the positions of peaks in a list of altitudes.
    // A peak is defined as an element that is greater than its immediate neighbors.
    // The positions are returned as 1-based indices.
    // If no peaks are found, it returns "No peaks".
    static def findPeaks(int n, List<Integer> altitude) {
        def peaks = []

        for (int i = 1; i < n - 1; i++) {
            if (altitude[i] > altitude[i - 1] && altitude[i] > altitude[i + 1]) {
                peaks.add(i + 1)  // Use 1-based index
            }
        }

        if (peaks.isEmpty()) {
            return "No peaks"
        } else {
            return peaks.join(" ")
        }
    }

    // hikingAlexHomecoming is a wrapper function that calls findPeaks.
    // It takes the number of points and a list of altitudes, then returns the peaks.
    static def hikingAlexHomecoming(int n, List<Integer> altitudes) {
        return findPeaks(n, altitudes)
    }
}

assertions:
void testFindPeaks() {
    // Test case 1: Peaks at positions 2 and 4
    assert MainClass.hikingAlexHomecoming(5, [1, 3, 2, 4, 1]) == "2 4"

    // Test case 2: No peaks
    assert MainClass.hikingAlexHomecoming(4, [1, 2, 3, 4]) == "No peaks"

    // Test case 3: Peaks at positions 3 and 5
    assert MainClass.hikingAlexHomecoming(6, [5, 4, 5, 4, 5, 4]) == "3 5"

    // Test case 4: Peak at position 2
    assert MainClass.hikingAlexHomecoming(3, [10, 20, 10]) == "2"

    // Test case 5: No peaks (not enough points)
    assert MainClass.hikingAlexHomecoming(2, [1, 2]) == "No peaks"
}

testFindPeaks()

